mapper.select:
select b.* , p.proName as providerName from smbms_bill as b , smbms_provider as p where b.providerId = p.id

    and b.productName = #{productName}


    and b.providerId = #{providerId}


    and b.isPayment = #{isPayment}

mapper.update:
update smbms_bill
productName = #{productName}, productUnit = #{productUnit}, productCount = #{productCount}, totalPrice = #{totalPrice}, providerId = #{providerId}, isPayment = #{isPayment}, modifyBy = #{modifyBy}, modifyDate = #{modifyDate}
where id = #{id}
