mapper.select:
select * from smbms_provider where 1=1

    and proName like #{proName}


    and proCode like #{proCode}

mapper.update:
update smbms_provider set proCode = #{proCode} , proName = #{proName}
 , proContact = #{proContact} , proPhone = #{proPhone}
  , proAddress = #{proAddress} , proFax = #{proFax}
   , proDesc = #{proDesc} where id = #{id}
