mapper.select:
select * from smbms_provider


        proName like #{proName}


        and proCode like #{proCode}

mapper.update:
update smbms_provider
proCode = #{proCode}, proName = #{proName}, proContact = #{proContact}, proPhone = #{proPhone}, proAddress = #{proAddress}, proFax = #{proFax}, proDesc = #{proDesc}
where id = #{id}
