mapper.select:
select * from smbms_provider where 1=1

    and proName like #{proName}


    and proCode like #{proCode}

mapper.update:
update smbms_provider set proCode = #{proCode} and proName = #{proName}
 and proContact = #{proContact} andproPhone =#{proPhone}
  and proAddress#{proAddress} and proFax = #{proFax}
   and proDesc = #{proDesc} where id = #{id}
