mapper.select:
select count(1) as count from smbms_user u,smbms_role r where u.userRole = r.id

    and u.userName like #{userName}


    and u.userRole = #{userRole}

select u.* , r.roleName as userRoleName from smbms_user u , smbms_role r where u.userRole = r.id

    and u.userName like ${userName}


    and u.userRole = ${userRole}


    order by creationDate DESC limit ${currentPageNo},${pageSize}

mapper.update:
update smbms_user
userName = #{userName}, gender = #{gender}, birthday = #{birthday}, phone = #{phone}, address = #{address}, modifyBy= #{modifyBy}, modifyDate = #{modifyDate}
where id = #{id}
